import React, { useEffect, useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import VerticalGraph from './VerticalGraph';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, TextField, Button } from "@mui/material";

const Hero = () => {
  useEffect(() => {
    toast.success("Welcome To DASHBOARD !!");
  }, []);

  const [rows, setRows] = useState([{ name: "", value: 0 }]);

  // Handle input change
  const handleChange = (index, field, value) => {
    const newRows = [...rows];
    newRows[index][field] = field === "value" ? Number(value) || 0 : value;
    setRows(newRows);
  };

  // Add a new row
  const addRow = () => {
    setRows([...rows, { name: "", value: 0 }]);
  };

  //reset data
  const reset =()=>{
    setRows([{ name: "", value: 0 }])
  }

  return (
    <div>
      <ToastContainer position="top-center" autoClose={3000} hideProgressBar={false} newestOnTop={true} closeOnClick pauseOnFocusLoss draggable pauseOnHover />
      <div className="container">
        <div className="row">
          {/* Left Section (Chart & Upload Button) */}
          <div className="col-8">
            <VerticalGraph chartData={rows} /> {/* ✅ Pass chartData */}
          </div>
          <div className="col-4 mt-5">
          <label for="formFile" className="form-label">Default file input example</label>
          <input className="form-control" type="file" id="formFile"/>
              <button className="btn btn-outline-secondary d-block mx-auto">
                <i className="fa fa-upload mx-3" aria-hidden="true"></i>Upload File
              </button>
            </div>
            </div>

          {/* Right Section (Manual Data Entry Table) */}
          <div className="row">
            <TableContainer component={Paper} sx={{ marginTop: 2 }}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Name</TableCell>
                    <TableCell>Value</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {rows.map((row, index) => (
                    <TableRow key={index}>
                      <TableCell>
                        <TextField
                          variant="outlined"
                          size="small"
                          value={row.name}
                          onChange={(e) => handleChange(index, "name", e.target.value)}
                        />
                      </TableCell>
                      <TableCell>
                        <TextField
                          type="number"
                          variant="outlined"
                          size="small"
                          value={row.value}
                          onChange={(e) => handleChange(index, "value", e.target.value)}
                        />
                      </TableCell>
    
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
              <Button variant="contained" color="primary" onClick={addRow} sx={{ margin: 2 }}>
                Add Row
              </Button>

              <Button variant="contained" color="primary" onClick={reset} sx={{ margin: 2 }}>
                reset
              </Button>
            </TableContainer>
          </div>
        </div>
      </div>
  );
};

export default Hero;
