import React from "react";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const VerticalGraph = ({ chartData }) => {
  const options = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Dynamic Bar Chart" },
    },
  };

  // Convert table data into chart format
  const labels = chartData.map((item, index) => `Entry ${index + 1}`);
  const dataValues = chartData.map((item) => item.value);

  const data = {
    labels,
    datasets: [
      {
        label: "User Data",
        data: dataValues,
        backgroundColor: "rgba(53, 162, 235, 0.5)",
      },
    ],
  };

  return <Bar options={options} data={data} />;
};

export default VerticalGraph;
